import React, { useState } from 'react'
import './hotel.css'
import Navbar from '../../components/navbar/navbar'
import Header from '../../components/header/header'
import {MdLocationOn} from 'react-icons/md' 
import MailList from '../../components/maillist/MailList'
import Footer from '../../components/footer/Footer'
import {FaArrowAltCircleLeft} from 'react-icons/fa'
import {FaArrowAltCircleRight} from 'react-icons/fa'
import {MdCancel} from 'react-icons/md'

const Hotel = () => {

  const [slideNumber, setSlideNumber] = useState(0)
  const [open, setOpen] = useState(false)

  const photos = [
    {
      src: 'data:image/jpeg;base64,'
    }, 
    {
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQo9bEaPjDYew1Q_lV3g21YFVaFWIzwgIUFazIWftCh&s"
    },
    {
      src: "data:image/jpeg;base64,"
    },
    {
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQo9bEaPjDYew1Q_lV3g21YFVaFWIzwgIUFazIWftCh&s"
    },
    {
      src: "data:image/jpeg;base64,"
    },
    {
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQo9bEaPjDYew1Q_lV3g21YFVaFWIzwgIUFazIWftCh&s"
    },
  ];

  const handleOpen = (i) => {
    setSlideNumber(i);
    setOpen(true);
  }

  const handleMove = (direction) => {
    let newSlideNumber;
    if(direction === "l"){
      newSlideNumber = slideNumber === 0 ? 5 : slideNumber - 1
    }
    else
    {
      newSlideNumber = slideNumber === 5 ? 0 : slideNumber + 1
    }

    setSlideNumber(newSlideNumber)
  }

  return (
    <div>
      <Navbar />
      <Header type="list"/>
      <div className="hotel__container">
        {open && <div className="slider">
          <MdCancel className='close' onClick={() => setOpen(false)}/>
          <FaArrowAltCircleLeft className='arrow' onClick={() => handleMove("l")}/>
          <div className="silder__wrapper">
            <img src={photos[slideNumber].src} alt="" className="slider__img" />
          </div>
          <FaArrowAltCircleRight className='arrow' onClick={() => handleMove("r")}/>
        </div>}
        <div className="hotel__wrapper">
          <button className='book__now'>Reserve Or Book Now!</button>
          <h1 className="hotel__title">Grand Hayatt</h1>
          <div className="location__address">
            <MdLocationOn/>
            <span>48 Stree Karwe Nagar Pune</span>
          </div>
          <span className="hotel__distance">Excellent Location - Close to Railway Station</span>
          <span className="hotel__price__offer">Book Now and get Free taxi to pick you up at the Airport</span>
          <div className="hotel__images">
            
            {
              photos.map((photo,i) => (
                <div className="hotel__image__wrapper">
                  <img onClick={() => handleOpen(i)} src={photo.src} alt="" className="hotel__img" />
                </div>
              ))
            }
          </div>
          <div className="hotel__details">
            <div className="hotel__details__texts">
              <h1 className="hotel__title">Stay at your dream place</h1>
              <p className="hotel__desc">Lorem ipsum dolor, sit amet consectetur adipisicing elit. Voluptates consequuntur ipsum harum, ab officiis placeat sed nulla quas, quasi delectus dolorum sunt velit vitae sint unde. Necessitatibus, impedit sunt autem vel maiores molestiae porro aliquam explicabo consequuntur harum libero, facilis eos omnis eum aut corporis eveniet? Cumque voluptate, illo ipsam omnis commodi repudiandae nostrum unde, praesentium vitae cum, doloremque nihil modi? Molestiae rem voluptate quae veritatis. Nostrum deserunt facere voluptatibus.</p>
            </div>
            <div className="hotel__details__price">
              <h1>Perfect for a night's Stay</h1>
              <span>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Fuga eveniet incidunt alias suscipit molestiae aliquam eius culpa error id quod.</span>
              <h2>
                <b>Rs 3000</b> (5 nights)
              </h2>
              <button>Reserve or Book now!</button>
            </div>
          </div>
        </div>
        <MailList/>
        <Footer/>
      </div>
    </div>
  )
}

export default Hotel
